INSERT INTO `topic` VALUES ('7', '学生管理系统', 'admin', '14781828227', '1.能按班级、按课程完成对学生成绩的增删改查；\r\n2.能按班级、按课程统计学生的成绩，能求总分、平均分，课程的不及格人数等；\r\n3.能按班级、按课程对学生的成绩进行排序；\r\n4.能查询某名学生的成绩。\r\n5.论文要救思路清晰，结构合理，语言流畅，书写格式符合要求。', '2019-10-31 20:22:31');
